void get_xy_speed_from_ang(s16 ang, s16 speed_mod, s32 *x_speed, s32 *y_speed)
{
	ang = roll_s16(ang-90,0,360);
	//eg mult 256 * mod
	//draw_text(0,8,true,"%4d", ang+90 );

	*x_speed = COS[ang] * speed_mod;
	*y_speed = SIN[ang] * speed_mod;
}






void modify_xy_speed_on_collision( u8 collision, s32 *x_speed, s32 *y_speed, s32 speed_mod, s32 min_speed, s32 max_speed)
{
	if ( collision & DIR_BIT_N )
		*y_speed = abs(*y_speed) + speed_mod ;

	if ( collision & DIR_BIT_S )
		*y_speed = - ( abs(*y_speed) + speed_mod );
			
	if ( collision & DIR_BIT_W )
	 	*x_speed = abs(*x_speed) + speed_mod ;

	if ( collision & DIR_BIT_E )
		*x_speed = - ( abs(*x_speed) + speed_mod );




	if ( *x_speed < 0 && *x_speed > -min_speed )
		*x_speed = -min_speed;

	if ( *x_speed > 0 && *x_speed < min_speed )
		*x_speed = min_speed;

	if ( *y_speed < 0 && *y_speed > -min_speed )
		*y_speed = -min_speed;

	if ( *y_speed > 0 && *y_speed < min_speed )
		*y_speed = min_speed;




	if ( *x_speed < 0 && *x_speed < -max_speed )
		*x_speed = -max_speed;

	if ( *x_speed > 0 && *x_speed > max_speed )
		*x_speed = max_speed;

	if ( *y_speed < 0 && *y_speed < -max_speed )
		*y_speed = -max_speed;

	if ( *y_speed > 0 && *y_speed > max_speed )
		*y_speed = max_speed;


}


s32 bigger(s32 a, s32 b)
{
	if (a>b) return a;
	return b;
}

u8 dir_bit_to_dir( u8 dir_bit )
{
	if ( dir_bit == DIR_BIT_N )		return DIR_N;
	if ( dir_bit == DIR_BIT_NE )	return DIR_NE;
	if ( dir_bit == DIR_BIT_E )		return DIR_E;
	if ( dir_bit == DIR_BIT_SE )	return DIR_SE;
	if ( dir_bit == DIR_BIT_S )		return DIR_S;
	if ( dir_bit == DIR_BIT_SW )	return DIR_SW;
	if ( dir_bit == DIR_BIT_W )		return DIR_W;
	if ( dir_bit == DIR_BIT_NW )	return DIR_NW;

	return DIR_COUNT;
}




u16 getang8 (s32 x, s32 y)
{
	u16 ang;
	if (abs(y) > abs(x)) {
		ang = ((u32)abs(x<<8)) / ((u32)abs(y));	
		ang = (ang >= 107);
	} else {
		ang = ((u32)abs(y<<8)) / ((u32)abs(x));	
		ang = 2-(ang >= 107);
	}


	if (x<0) {
		if (y>0)
			return (8 - ang) & 7; 
		else
			return 4 + ang;
		}
	else if (y>0) 
		return ang; 
	else
		return 4 - ang;
}



u16 angle_to_player_from_player( a_hockey_player *player_a, a_hockey_player *player_b )
{
	return angle_to_coord( player_a->x, player_a->y, player_b->x, player_b->y);
}

u16 angle_to_coord( u32 x1, u32 y1, u32 x2, u32 y2)
{
	return get_angle_from_gradients( (x2>>8) - (x1>>8), (y2>>8) - (y1>>8));
}



