#define TAN_N(xgrad,ygrad) ( (xgrad<<6)/((ygrad==0? 2:ygrad)) )

u16 get_angle_from_gradients(s16 x_gradient, s16 y_gradient)
{
	u16 tan_n;
	u16 result;
//	a_mod = 0;
//	c = COS[a];
//	s = SIN[a];

	tan_n = TAN_N( abs(x_gradient) , abs(y_gradient) );

	result = A_TAN[tan_n];



	if ( x_gradient>0 && y_gradient>=0 )
		//return 111;
		result= (90-result);

	if ( x_gradient<=0 && y_gradient>0 )
		//return 222;
		result += 90 ;
		

	if ( x_gradient<0 && y_gradient<=0 )
		//return 333;
		result = (90-result) +180;
	
	if ( x_gradient>=0 && y_gradient<0 )
		//return 444;
		result += 270;
/*
	if (x_gradient<0 && y_gradient>=0)
		result = (90-result) + 90;

	if (y_gradient<0 && x_gradient<0)
		result+=180;
	
	if (y_gradient>=0 && x_gradient>=0)
		result = result;	//do nothing

	if (x_gradient>=0 && y_gradient<0)
		result = (90-result) + 270;
*/

	return roll_u16(result+90,0,360); // +- 90
}




