//returns number from -180 to 180
s16 difference_between_angles( u16 ang_a, u16 ang_b )
{
	s16 s_ang[2];
	s16 diff;
	
	diff = ang_a - ang_b;

	while(diff > 180 )
		diff -= 360;
	while(diff < -180 )
		diff += 360;

	return diff;
}


